mapper.select:

select * from
(select rownum rn, tt.* from
(select * from boardtable order by ref desc, step asc)tt
)where rn>=#{startNum} and rn<=#{endNum}


select * from
(select rownum rn, tt.* from
(select * from boardtable where ${searchOption} like '%'||#{keyword}||'%' 
					 order by ref desc, step asc)tt
)where rn>=#{startNum} and rn<=#{endNum}
